mapper.update:
UPDATE T_COMMON_CODE SET 
GRP_CD_DESC = #{grp_cd_desc},
DTL_CD_DESC = #{dtl_cd_desc}
WHERE GRP_CD = #{grp_cd} AND DEPTH = #{depth}
AND DTL_CD = #{dtl_cd}

UPDATE T_COMMON_CODE SET ALGN_ORD = ALGN_ORD - 1
WHERE GRP_CD = #{grp_cd}
	AND DEPTH = #{depth}
	AND ALGN_ORD > #{algn_ord}

UPDATE T_COMMON_CODE SET ALGN_ORD = ALGN_ORD + 1
WHERE GRP_CD = #{grp_cd}
	AND DEPTH = #{depth}
	AND ALGN_ORD >= #{algn_ord}

UPDATE T_COMMON_CODE SET 
	ALGN_ORD = #{algn_ord}
WHERE GRP_CD = #{grp_cd}
	AND DEPTH = #{depth}
	AND DTL_CD = #{dtl_cd}

mapper.delete:
DELETE FROM T_COMMON_CODE
WHERE GRP_CD = #{grp_cd}
	AND DEPTH = #{depth}
	AND DTL_CD = #{dtl_cd}

mapper.update:
UPDATE T_COMMON_CODE SET USE_YN = #{use_yn}
WHERE GRP_CD = #{grp_cd}
	AND DEPTH = #{depth}
	AND DTL_CD = #{dtl_cd}
