mapper.insert:
/*com.ncomz.nms.dao.admin.project.ProjectMapper.insertProjectInfo*/
INSERT INTO T_PROJECT_INFO(
	  PJT_NO
	, PJT_NAME
	, CHECK_YN
	, PJT_TYPE_CD
	, CUST_NM
	, PM_NM
	, DEPT_CD
	, TEAM_CD
	, CONT_YN
	, USE_YN
	, STRT_DT
	, END_DT
	, ETC_YN
	, CRE_ID
	, CRE_DT
	, UPD_ID
	, UPD_DT
	) VALUES(
		  #{pjt_no}
		, #{pjt_name}
		, #{check_yn}
		, #{pjt_type_cd}
		, #{cust_nm}
		, #{pm_nm}
		, #{dept_cd}
		, #{team_cd}
		, #{cont_yn}
		, #{use_yn}
		, #{strt_dt}
		, #{end_dt}
		, #{etc_yn}
		, #{cre_id}
		, now()
		, #{cre_id}
		, now()
	)

/*com.ncomz.nms.dao.admin.project.ProjectMapper.insertMember*/
INSERT INTO T_PROJECT_USER_INFO(
	  PJT_NO
	, SEQ
	, EMP_NO
	, STRT_DT
	, END_DT
	, WORK_DT
	, WORK_ECT
	, CRE_ID
	, CRE_DT
	, UPD_ID
	, UPD_DT
) VALUES(
	  #{pjt_no}
	, (
		SELECT  IFNULL(MAX(SEQ),0)+1
		FROM T_PROJECT_USER_INFO PU
	  )
	, #{emp_no}
	, REPLACE(#{mem_strtdt},'-','')
	, REPLACE(#{mem_enddt},'-','')
	, #{work_dt}
	, #{work_ect}
	, #{cre_id}
	, NOW()
	, #{cre_id}
	, NOW()
)

mapper.select:
/*com.ncomz.nms.dao.admin.project.ProjectMapper.chkPjtNo*/
SELECT COUNT(PJT_NO)
FROM T_PROJECT_INFO
WHERE PJT_NO = #{pjt_no}

mapper.update:
/*com.ncomz.nms.dao.admin.project.ProjectMapper.projectUpdate*/
UPDATE T_PROJECT_INFO
SET 
	  PJT_NO = #{pjt_no}
	, PJT_NAME = #{pjt_name}
	, PJT_TYPE_CD = #{pjt_type_cd}
	, CHECK_YN = #{check_yn}
	, CUST_NM = #{cust_nm}
	, PM_NM = #{pm_nm}
	, DEPT_CD = #{dept_cd}
	, TEAM_CD = #{team_cd}
	, CONT_YN = #{cont_yn}
	, USE_YN = #{use_yn}
	, STRT_DT = #{strt_dt}
	, END_DT = #{end_dt}
	, ETC_YN = #{etc_yn}
	, UPD_ID = #{cre_id}
	, UPD_DT = now()
WHERE PJT_NO = #{pre_pjt_no}
